import '../css/categoryDropdown.style.css'
import categoryList from '../resources/categoryList.js'
import { faCaretLeft, faCaretRight } from "@fortawesome/free-solid-svg-icons";
import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import SubCategoryDropdown from './SubCategoryDropdown';

let tog = {toggle: false}
function CategoryDropdown(props) {
  const [toggle, setToggle] = useState(categoryList)

  const toggleHandle = (name) => {
    const prevToggle = toggle
    
    const index = toggle.findIndex((each => each.title == name));    
    prevToggle[index].toggle = !prevToggle[index].toggle
    setToggle(prevToggle => {return prevToggle})
  }

  return (
    <div class="CategoryCard">
      <div class="dropdown-menu">
        <div class="dropdown-pad">
          {toggle.map((each) =>
            <div class="main-category-div">
              <div class='category-list'>{each.title}</div>
              {!each.toggle && <FontAwesomeIcon icon={faCaretRight} class="toggle-icon" onClick={(e) => toggleHandle(each.title)} />}
              {each.toggle && <FontAwesomeIcon icon={faCaretLeft} class="toggle-icon" onClick={(e) => toggleHandle(each.title)} />}
              {each.toggle && <SubCategoryDropdown subCategory={each.subCategory} />}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default CategoryDropdown
