import React from 'react'
import '../../../css/searchbox.style.css'
import { faCartShopping, faCaretDown, faCaretUp,faCaretLeft } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const Searchbox = (props) => {

    const searchHandle= () => {
        let searchInput = document.getElementById("searchText").value
        document.getElementById("username-element").innerHTML = searchInput        
        props.searchResultHandle(searchInput)
        props.pageHandle("Search")
    }
    return (
        <div class="Searchbox">
            {/* <div class='text-center search-header'> ค้นหาสินค้าของคุณได้เลยคะ^^</div> */}
            <div class="searchbox-container">
            {/* <div class="search"> */}
                <input type="text" name="" placeholder="Serach" class="search-input-container" id='searchText'
                    onKeyPress={event => {
                    if (event.key === 'Enter') {
                        props.searchResultHandle(event.target.value)
                        props.pageHandle("Search")
                    }
                }}
                />
            <FontAwesomeIcon icon={faCaretDown} class="btn" onClick={(e) => searchHandle()} />
            {/* </div> */}
            </div>
        </div>
    )
}

export default Searchbox;
