import React from 'react'
import '../../../css/payment.css'
import {
  InputGroup,
  FormControl,
  Col,
  Row,
  Button,
  Container,
  Form,
  Table,
  Modal,
  FloatingLabel,
} from "react-bootstrap";

function Payment(props) {
  // return (


  //   <div className="text-center">


  //     <div className="container">


  //       <div class="col box-cart-main">

  //         <div class="row ">


  //           <div class="text-cart-header"><center><h1>Payment</h1></center></div>

  //         </div>


  //         <div className="col box-cart-table">
  //           <div class="column"><center><b>HEADER LIST</b></center></div>
  //           <div class="header-table">
  //             <center>
  //               <b></b>
  //             </center>
  //             <hr></hr>
  //           </div>

  //         </div>
  //         <div className="col box-cart-table">
  //           <div class="column"><center><b>HEADER LIST</b></center></div>
  //           <div class="header-table">
  //             <center>
  //               <b></b>
  //             </center>
  //             <hr></hr>
  //           </div>

  //         </div>


  //         <center>
  //           <div class="component-btn" onClick={(e) => props.pageHandle("Reciept","Home")}>
  //             <center>
  //               <h2>Complete(go Home)</h2>
  //             </center>
  //           </div>
  //         </center>
  //       </div>
  //     </div>
  //   </div>

  //   // <div >Payment
  //   //   <div class="component-btn" onClick={(e) => props.pageHandle("Reciept","Home")}> Complete(go Home)</div>
  //   // </div>
  // )
  const [username_cookie, setUsername_cookie, removeUsername_cookie] =
  useCookies(["username_tkn"]);

  const [picture, setPicture] = useState("");

  const [promtpay_preview, setPromtpay_preview] = useState("images/testpromptpay.jpg");

  const [bank_preview, setBank_preview] = useState("images/KBANK_LOGO.png");

  const { clickSTic, setclickSTic } = useContext(UserContextSTic);
  const { clickDTic, setclickDTic } = useContext(UserContextDTic);

  const Ans = useMemo(() => {
    if (clickSTic == "สถานีต้นทาง" || clickDTic == "สถานีปลายทาง") {
      return;
    } else {
      return dijkstra(graph, clickSTic.split(" ")[0], clickDTic.split(" ")[0]);
    }
  }, [clickSTic, clickDTic]);


  const [Count, setCount] = useState(0);
  const { clickCountTic, setclickCountTic } = useContext(UserContextCountTic);
  const { Dates, setDates } = useContext(UserContextDate);

 

  function handlePromtpay(event) {
    if (event.target.files[0]) {
      setPicture(event.target.files[0]);
      setPromtpay_preview(URL.createObjectURL(event.target.files[0]));
    }
  }

  function handleBank(event) {
    if (event.target.files[0]) {
      setPicture(event.target.files[0]);
      setBank_preview(URL.createObjectURL(event.target.files[0]));
    }
  }

  function refreshPage() {
    window.scrollTo(0, 0);
  }
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);

  const [show5, setShow5] = useState(false);
  const handleClose5 = () => setShow5(false);
  const handleShow5 = () => setShow5(true);

  var split_date = Dates.split('-');
  var exp_year = parseInt(split_date[0]);
  var exp_mth = parseInt(split_date[1]);
  var exp_day = parseInt(split_date[2]);
  console.log(exp_year)
  console.log(exp_mth)
  console.log(exp_day)
  var exp_date = new Date(exp_year, exp_mth, exp_day)

    function addDays(theDate, days) {
      return new Date(theDate.getTime() + days*24*60*60*1000);
    }
    var exp_date_plus = addDays(exp_date, 15);
    var temp_mth = exp_date_plus.getMonth()
    var temp_year = exp_date_plus.getFullYear()
    if (exp_date_plus.getMonth() ===0){
      temp_mth = 12
    }

    if (16 <= exp_day && exp_day<= 31 && exp_mth === 12){
      console.log("change year")
    }
    else
    {
      temp_year -= 1
    }

  const [show6, setShow6] = useState(false);
  const handleClose6 = () => setShow6(false);
  const handleShow6 = () => setShow6(true);
  
  

    console.log(exp_date_plus)

  const handleClose1 = () => {
    setCount("รูปแบบการชำระเงิน");
    setPromtpay_preview("");
    setBank_preview("images/KBANK_LOGO.png");
    setPicture("");


    axios
      .post("https://us-central1-soft-dev-tutorial.cloudfunctions.net/Ticket", {
        Date_buy: Dates,
        Date_exp: temp_year.toString() + "-" + ('00'+(temp_mth.toString())).slice(-2) + "-" + ('00'+(exp_date_plus.getDate().toString())).slice(-2),
        S_Source: clickSTic,
        S_Dest: clickDTic,
        ticket_id: ticket_id,
        user_customer: username_cookie["username_tkn"],
        Amount : clickCountTic
      })
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      })
    

  };



  const handleClose2 = () => {
    setCount("รูปแบบการชำระเงิน");
    setBank_preview("images/KBANK_LOGO.png");
    setPromtpay_preview("images/testpromptpay.jpg");
    setPicture("");
  };

  const handleShow = () => {
    setShow(true);
  };



  const DropdownItem = () => {
    return (
      <div>
        {["Promptpay", "โอนผ่านธนาคาร", "บัตรเครดิต"].map((item) => (
          <Dropdown.Item
            key={item}
            onClick={() => {
              setCount(item);
              handleClose();
            }}
          >
            {item}
          </Dropdown.Item>
        ))}
      </div>
    );
  };

  if (Ans)
    return (
      <div style={{ marginTop: "10px", width: "100%" }}>
        <h1 align="center">Payment</h1>
        <Row
          style={{
            borderRadius: "20px",
            width: "auto",
            height: "23px",
            margin: "1px",
            backgroundColor: "#2F2F35",
            color: "#ffff",
            fontsize: "20px",
            fontWeight: "bold",
          }}
        ></Row>
        <br></br>
        <br></br>
        <br></br>
        <Container>
          <Row>
            <Col></Col>

            <Col style={{ textAlign: "center" }}>
              <Col
               style={{
            backgroundColor: "#93A9A3",
            borderRadius: "12px",
            width: "70vw",
            textAlign: "center",
            borderStyle: "solid",
            borderColor: "#7E7D81",
            margin: "auto",
          }}
              >
                <h1 style={{color:"#F9F9F8"}}>
                  <i class="fas fa-check-double" ></i> Confirm Order
                </h1>
                <hr />
                <br></br>
                <Col
                  style={{
                    backgroundColor: "#A4C0BF",
                    borderRadius: "12px",
                    width: "40vw",
                    margin: "auto",
                    color: "#F9F9F8",
                    borderStyle: "solid",
                    borderColor: "#7E7D81",
                    borderWidth: "5px",
                    margin: "auto",
                  }}
                >
                  <br></br>
                  <h4 align="center">
                    หมายเลขคำสั่งซื้อ : {ticket_id}
                  </h4>
                  <h4 align="center">ชื่อผู้ใช้ : {" " +username_cookie["username_tkn"]}</h4><br/>
                 
                </Col>
                <br></br>
                <br></br>
                <h5
                  style={{
                    whiteSpace: "pre",
                    backgroundColor: "#F9F9F8",
                    borderRadius: "12px",
                    width: "40vw",
                    textAlign: "center",
                    marginLeft: "20px",
                    borderStyle: "solid",
                    borderColor: "#7E7D81",
                    borderWidth: "5px",
                    margin: "auto",
                  }}
                >
                   <br/><h4 align="center">เส้นทางโดยสาร : </h4><br/>{clickSTic} <br></br> <i class="fas fa-arrow-down"></i>
                  <br></br> {clickDTic}
                  <br/><br/></h5>
                <br></br> <br/>
                <Col
                  style={{
                    backgroundColor: "#A4C0BF",
                    borderRadius: "12px",
                    width: "40vw",
                    margin: "auto",
                    color: "#F9F9F8",
                    borderStyle: "solid",
                    borderColor: "#7E7D81",
                    borderWidth: "5px",
                    margin: "auto",
                  }}
                >
                  <h4 align="center">
                    <br/>ระยะเวลาที่ใช้โดยประมาณ : {Ans?.[2]} นาที
                  </h4>
                  <h4 align="center"> ราคา : {Ans?.[1]} บาท</h4>
                  <h4 align="center"> จำนวน : {clickCountTic} ชิ้น</h4>
                  <h4 align="center">
                    {" "}
                    ราคารวม : {Ans?.[1] * clickCountTic} บาท
                  </h4><br/>
                </Col>
                <br></br>
                <br></br>
                <Col
                  style={{
                    backgroundColor: "#A4C0BF",
                    borderRadius: "12px",
                    width: "40vw",
                    margin: "auto",
                    color: "#F9F9F8",
                    borderStyle: "solid",
                    borderColor: "#7E7D81",
                    borderWidth: "5px",
                    margin: "auto",
                  }}
                >
                  <h4 align="center">
                  <br/>วันที่ซื้อ : {Dates}
                  </h4>
                  <h4 align="center">
                    วันที่หมดอายุ : {temp_year.toString() + "-"}{('00'+(temp_mth.toString())).slice(-2)}{"-" + ('00'+(exp_date_plus.getDate().toString())).slice(-2)}
                  </h4><br/>
                </Col>
                <Link to="/ticket">
                <br/><Button
                    variant="warning"
                    style={{
                      marginBottom: "5px",
                      width: "100px",
                      whiteSpace: "pre",
                      margin: "10px",
                    }}
                  >
                    แก้ไข
                  </Button>
                </Link>
                <Button
                  onClick={() => handleShow()}
                  variant="success"
                  style={{
                    marginBottom: "5px",
                    width: "100px",
                    whiteSpace: "pre",
                    margin: "10px",
                  }}
                >
                  ยืนยัน
                </Button>
                <Modal show={show} onHide={handleClose} centered>
                  <Modal.Header closeButton>เลือกวิธีการชำระเงิน</Modal.Header>
                  <Modal.Body style={{ margin: "auto" }}>
                    <h1>รูปแบบการชำระเงิน</h1><hr/><br/>
                    <Dropdown
                      style={{
                        width: "100px",
                        textAlign: "center",
                        marginLeft: "25%",
                      }}
                    >
                      <Dropdown.Toggle
                        variant="success"
                        style={{ textAlign: "center" }}
                      >
                        {Count ? Count : "รูปแบบการชำระเงิน"}
                      </Dropdown.Toggle>
                      <Dropdown.Menu>
                        <DropdownItem />
                      </Dropdown.Menu>
                    </Dropdown><br/><br/>
                  </Modal.Body>
                 
                </Modal>
                {/* ------------------------------------- PromptPay ------------------------------------------------------------------ */}
                <Modal
                  show={Count == "Promptpay" ? true : false}
                  onHide={handleClose2}
                  centered
                >
                  <Modal.Header closeButton>
                    รูปแบบการชำระเงิน : PromptPay{" "}
                  </Modal.Header>
                  <Modal.Body>
                    <center>
                      <img
                        src={promtpay_preview}
                        width="300"
                        height="auto"
                        style={{
                          textAlign: "center",
                          margin: "20px",
                          borderRadius: "30px",
                        }}
                      ></img>
                    </center>
                    <Form.Group
                      controlId="formFileMultiple"
                      className="mb-3"
                      onSubmit={handleClose1}
                    >
                      <Form.Label>Upload You Slip Payment</Form.Label>
                      <Form.Control
                        type="file"
                        accept="image/*"
                        multiple
                        name="picture"
                        onChange={handlePromtpay}
                      />
                    </Form.Group>
                  </Modal.Body>
                  <Modal.Footer>
                    <Link to="/show-ticket">
                    <Button
                      variant="secondary"
                      onClick={handleClose1}
                      type="submit"
                    >
                      OK
                    </Button>
                    </Link>
                  </Modal.Footer>
                </Modal>
                {/* ------------------------------------- โอนผ่านธนาคาร ------------------------------------------------------------------ */}
                <Modal
                  show={Count == "โอนผ่านธนาคาร" ? true : false}
                  onHide={handleClose2}
                  centered
                  style={{ width: "500", height: "auto", borderRadius: "30px" }}
                >
                  <Modal.Header closeButton>
                    รูปแบบการชำระเงิน : โอนผ่านธนาคาร
                  </Modal.Header>
                  <Modal.Body>
                    <Container>
                      <Col>
                        <Row style={{ textAlign: "center" }}>
                          <Col>
                            <h4>
                              <b>โอนผ่านธนาคาร</b>
                            </h4>
                            <center>
                              <img
                                src={bank_preview}
                                width="100"
                                height="auto"
                                style={{
                                  textAlign: "center",
                                  margin: "20px",
                                  borderRadius: "30px",
                                }}
                              ></img>
                            </center>
                            <h7 align="left" width="auto">
                              {" "}
                              ธนาคารกสิกรไทย{" "}
                            </h7>
                            <br></br>
                            <h7 align="left">
                              {" "}
                              ชื่อบัญชี : นายอภิรักษ์ อุลิศ{" "}
                            </h7>
                            <br></br>
                            <h7 align="left"> เลขที่บัญชี :040-325-288-3</h7>
                            <br></br>
                            <h7 align="left"> สาขาบัญชี : เดอะมอลล์บางกะปิ </h7>
                            <br></br>
                            <br></br>

                            <Form.Group
                              controlId="formFileMultiple"
                              className="mb-3"
                            >
                              <Form.Label>Upload You Slip Payment</Form.Label>
                              <Form.Control
                                type="file"
                                accept="image/*"
                                multiple
                                onChange={handleBank}
                              />
                            </Form.Group>
                          </Col>
                        </Row>
                      </Col>
                    </Container>
                  </Modal.Body>
                  <Modal.Footer>


                 
                    <Button
                      variant="secondary"
                      onClick={handleClose1}
                      type="submit"
                    >
                      OK
                    </Button>
                    
                  </Modal.Footer>
                </Modal>
                {/* ------------------------------------- บัตรเครดิต ------------------------------------------------------------------ */}
                <Modal
                  show={Count == "บัตรเครดิต" ? true : false}
                  onHide={handleClose2}
                  centered
                  style={{ width: "500", height: "auto", borderRadius: "30px" }}
                >
                  <Modal.Header closeButton>
                    รูปแบบการชำระเงิน : บัตรเครดิต
                  </Modal.Header>
                  <Modal.Body>
                    <Container>
                      <Col>
                        <Row style={{ textAlign: "center" }}>
                          <Col>
                            <h4>
                              <b>บัตรเครดิต</b>
                            </h4>

                            <div
                              width="500"
                              height="300"
                              style={{
                                textAlign: "center",
                                margin: "20px",
                                borderRadius: "30px",
                                backgroundColor: "#0C9EA8",
                              }}
                            >
                              <p>...........................</p>
                            </div>

                            <FloatingLabel
                              controlId="floatingInput"
                              label="หมายเลขบัตร"
                              type="number"
                              
                              className="mb-3"
                            >
                              <Form.Control
                                type="number"
                                maxlength="16"
                                placeholder="name@example.com"
                                defaultValue="4546320488952222"
                              />
                            </FloatingLabel>
                            <FloatingLabel
                              controlId="floatingPassword"
                              type="number"
                              
                              
                              label="CVC"
                            >
                              <Form.Control
                                type="password"
                                maxlength="3"
                                placeholder="Password"
                                defaultValue="456"
                              />
                            </FloatingLabel>
                            <FloatingLabel
                              controlId="floatingPassword"
                              label="EXP"
                              
                            >
                              <Form.Control
                                type="date"
                                placeholder="Password"
                                defaultValue="2022-12-12"
                              />
                            </FloatingLabel>

                            <FloatingLabel
                              controlId="floatingPassword"
                              label="Fname"
                             
                            >
                              <Form.Control
                                type="fname"
                                placeholder="Password"
                                defaultValue="APIRAK"
                              />
                            </FloatingLabel>
                            <FloatingLabel
                              controlId="floatingPassword"
                              label="Lname"
                              
                            >
                              <Form.Control
                                type="lname"
                                placeholder="Password"
                                defaultValue="OULIS"
                              />
                            </FloatingLabel>
                            <br></br>
                          </Col>
                        </Row>
                       
                      </Col>
                    </Container>
                  </Modal.Body>
                  <Modal.Footer>
                  
                    <Button
                      variant="secondary"
                      onClick={handleShow6}
                      type="submit"
                    >
                      OK
                    </Button>
                    <Modal show={show6} onHide={handleClose6} centered>
                    <Modal.Header closeButton><i class="fas fa-newspaper"></i><p>. </p><b>ระบบ RTS</b></Modal.Header>
                            <Modal.Body>
                            <center><img src="images/RTS-Logo.png" width = '100' height='auto'style={{textAlign:"center", margin:"20px"}}></img></center>
                            <center><h2>RTS</h2></center>
                            <center><h1>สถานะการชำระเงิน </h1></center><br/>
                            <center><h4 class="text-success" >SUCESS  ชำระเงินสำเร็จ</h4></center>
                            </Modal.Body>
                            <Modal.Footer>
                            <Link to="/show-ticket">
                                <Button variant="secondary">
                                    OK
                                </Button>
                            </Link>
                        </Modal.Footer>
                    </Modal>
                   
                   
                  </Modal.Footer>
                </Modal>
                
                <Button variant="danger" onClick={handleShow5} style={{
                    marginBottom: "5px",
                    width: "100px",
                    whiteSpace: "pre",
                    margin: "10px",
                  }}> 
                  ยกเลิก
                   </Button>
                   <Modal show={show5} onHide={handleClose5} centered>
                    <Modal.Header closeButton><i class="fas fa-newspaper"></i><p>. </p><b>ระบบ RTS</b></Modal.Header>
                            <Modal.Body>
                            <center><img src="images/RTS-Logo.png" width = '100' height='auto'style={{textAlign:"center", margin:"20px"}}></img></center>
                            <center><h2>RTS</h2></center>
                            <center>ท่านต้องการยกเลิกรายการเพื่อกลับไปยังหน้าหลัก ใช่ไหม? </center>
                            </Modal.Body>
                            <Modal.Footer>
                            <Link to="/home">
                                <Button variant="secondary">
                                    OK
                                </Button></Link>
                        </Modal.Footer>
                    </Modal>
              
              </Col>
            </Col>

            <Col></Col>
          </Row>
        </Container>
      </div>
    );
  else {
    return <Redirect to="/ticket" />;
  }



}

export default Payment
