import '../css/categoryDropdown.style.css'
import categoryList from '../resources/categoryList.js'
import { faCaretLeft,faCaretRight } from "@fortawesome/free-solid-svg-icons";
import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import SubCategoryDropdown from './SubCategoryDropdown';

function CategoryDropdown(props) {
  const [toggle,setToggle] = useState([categoryList.map((each) => { name: each.name;
                                                                    keyword: each.keyword;
                                                                    subCategory: each.subCategory;
                                                                    toggle: false})])

  const toggleHandle = () =>{
    setToggle(!toggle)
  }

  return (
    <div class="CategoryCard"> 
        <div class = "dropdown-menu">
            <div class="dropdown-pad">
              {categoryList.map((each) => 
                <div class="main-category-div">
                  <div class='category-list'>{each.title}</div>
                  {!toggle && <FontAwesomeIcon icon={faCaretRight} class="toggle-icon" onClick={(e) => toggleHandle()}/>}
                  {toggle && <FontAwesomeIcon icon={faCaretLeft} class="toggle-icon" onClick={(e) => toggleHandle()}/>}
                  {toggle && <SubCategoryDropdown subCategory={each.subCategory}/>}
                </div>
              )}
            </div>
        </div>
    </div>
  )
}

export default CategoryDropdown
